var _request = y.rest(url);
var _data = _request;
if(type == 'post') {
    _data = _request.accept('text/html').contentType("application/x-www-form-urlencoded").post(requestdata).response;
    if(xpath) {
        var xdata = y.xpath(_data,xpath);
        response.object = <postresult>{xdata}</postresult>;
    }
    else {
        response.object = <postresult>{_data}</postresult>;
    }
}
else {
    if(headers) {
        var _headers = headers.split(',');
        for (i = 0; i < _headers.length; i++) {
            if(_headers[i].trim().split(':')[1].toLowerCase() == 'accept') {
                _data += _request.accept("'" + _headers[i].trim().split(':')[1].toLowerCase() + "'");
            }
            else {
                _data += _request.header("'" + _headers[i].trim().split(':')[0] + "'" + ',' + "'" + _headers[i].trim().split(':')[1] + "'");
            }
        }
        _data = _request.get(requestdata).response;
    }
    else {
        _data = _request.accept('application/json').get(requestdata).response;
    }

    if(xpath) {
        var xdata = y.xpath(_data,xpath);
        response.object = <getresult>{xdata}</getresult>;
    }
    else {
        response.object = <getresult>{_data}</getresult>;
    }
}
