select.execute:

var headers = header.split(',');
var headerString = '';
for (i = 0; i < headers.length; i++) {
    headerString += "'" + headers[i].trim().split(':')[0] + "'" + ',' + "'" + headers[i].trim().split(':')[1] + "'" + ',';
}

var _request = y.rest(url);
if(type == 'post') {
    if(header) {
        var _data = _request.header(headerString.substring(0,headerString.length - 1)).contentType("application/x-www-form-urlencoded").post(data).response;
    }
    else {
        var _data = _request.accept('text/html').contentType("application/x-www-form-urlencoded").post(data).response;
    }
    if(xpath) {
        var xdata = y.xpath(_data,xpath);
        response.object = <postresult>{xdata}</postresult>;
    }
    else {
        response.object = <postresult>{_data}</postresult>;
    }
}
else {
    if(header) {
        var _data = _request.header(headerString.substring(0,headerString.length - 1));
    }
    else {
        var _data = _request.get(data).response;
    }
    if(xpath) {
        var xdata = y.xpath(_data,xpath);
        response.object = <getresult>{xdata}</getresult>;
    }
    else {
        response.object = <getresult>{_data}</getresult>;
    }
}
