select.execute:

var url = "http://ibge.gov.br/cidadesat/xtras/pesqmun.php?pesqmun="+query;
var xpath = '//ul[@id="lista"] /li/a[@href]';
var q = y.query("SELECT * FROM html WHERE url=@url AND xpath=@xpath AND charset=@charset", {url:url,xpath:xpath,charset:"latin1"});
var data = <results />
for each (r in q.results..a) { 
    var raw_item = r.@href.text().toString()
    var items = raw_item.substring( raw_item.indexOf("(")+1, raw_item.indexOf(")") ).replace(/'/g, "").split(",")
    data.results += <city><name>{ unescape(items[0]) }</name><ibge_code>{items[1]}</ibge_code><state>{items[2]}</state></city>
}
response.object = data;
