select.execute:

y.include("https://raw.githubusercontent.com/nicksonjean/yql-tables/master/utils/scripts/random_useragent/random_useragent.min.js");
var _ua = random_useragent();
var _url = 'http://consultaoperadora.com.br/site2015';
var _request = y.rest(_url + '/resposta.php');
var _data = null;
y.log('Browser: ' + _ua.toString().replace(/,/g, ""));
_data = _request
            .accept('text/html')
            .contentType("application/x-www-form-urlencoded")
            .header('Accept-Encoding', 'gzip,deflate')
            .decompress(true)
            .header('User-Agent', _ua)
            .post('tipo=consulta&numero='+tel)
            .response;

var _root = y.xpath(_data,'//div[@id="resultado_num"] /div[@class="row"]');
var _result = <phone />

var carrier = y.xpath(_data, '//div[@id="resultado_num"] /div[@class="row"]/div[@class="col-md-8"]/span[2]/text()').toString();
y.log('Carrier: ' + carrier);
    _result.phone += <carrier>{ carrier }</carrier>

var logo = y.xpath(_data, '//div[@id="resultado_num"] /div[@class="row"]/center/div/img[@src]').toString();
y.log('Logo: ' + logo);
    _result.phone += <logo>{ logo }</logo>

var number = y.xpath(_data, '//div[@id="resultado_num"] /div[@class="row"]/div[@class="col-md-8"]/span[6]/text()').toString();
y.log('Number: ' + number);
    _result.phone += <number>{ number }</number>

var ported = y.xpath(_data, '//div[@id="resultado_num"] /div[@class="row"]/div[@class="col-md-8"]/span[4]/text()').toString();
y.log('Ported ' + ported);
    _result.phone += <ported>{ ported }</ported>

response.object = _result;
